                       moveUpRow : function (i) {
                           if (i<1) return;
                           var t = Tabs.upgrader;
                           var q = queueData.list.splice(i,1);
                           queueData.list.splice(i-1,0,q[0]);

                           if (i == queueData.index)
                               queueData.index--;
                           else if (queueData.index == i-1)
                               queueData.index++;

                           saveQueueData();
                           t.buildQueueDisplay();
                       },

